// Obtém a mensagem do payload
let message = msg.payload;

// Verifica se a mensagem é "Connect"
if (message % message === 0) {
    return msg;
} else {
    return null;
}

// Verifica se há dados no payload
if (Array.isArray(msg.payload) && msg.payload.length > 0) {
    // Recupera o último status armazenado no contexto do fluxo
    let lastStatus = context.get('lastStatus') || '';

    // Obtém o status atual (último item do payload)
    let currentStatus = msg.payload[msg.payload.length - 1].status;

    // Compara o status atual com o status anterior
    if (lastStatus === currentStatus) {
        // Se o status atual for igual ao status anterior, não envia a mensagem
        return null;
    } else {
        // Atualiza o status armazenado no contexto
        context.set('lastStatus', currentStatus);

        // Define o payload como o status atual
        msg.payload = currentStatus;

        return msg;
    }
} else {
    // Se não há dados no payload ou o payload está vazio
    msg.payload = 'No data received';
    return msg;
}

// Obtém os dados do contexto de fluxo ou cria novos se não existirem
let porcent = context.get('porcentagem') || null;

// Verifica se a mensagem recebida não é uma mensagem inicial
if (msg.topic === "mig/porta/Status") {
    // Se a mensagem é number, atualiza a quantidade
    if (msg.payload === Number) {
        porcent = msg.payload;
        context.set('porcentagem', porcent);
    } 
    // Se a mensagem é um horário, atualiza o horário
    else if (msg.payload.match(/^\d{2}:\d{2}:\d{2}$/)) {
        horario = msg.payload;
        context.set('horario', horario);
    }
}

// Se ambos estado e horário estão disponíveis, cria um objeto com eles
if (porcent !== null && horario !== null) {
    msg.payload = {
        porcent: porcent,
        horario: horario
    };

    // Limpa os valores após criar o objeto para evitar associações erradas
    context.set('estado', null);
    context.set('horario', null);

    return msg;
}

// Se ainda não está pronto para enviar, retorna uma mensagem vazia
return null;

// Obtém o último ID armazenado no contexto global
let lastId = global.get('LastId') || 0;

// Incrementa o ID para a próxima inserção
let newId = lastId + 1;

// Verifica se a mensagem recebida é do tópico correto
if (msg.topic === "mig/porta/Status") {
    // Configura a mensagem para inserção no MySQL
    msg.topic = "INSERT INTO StatusHumid (ID, status, datahora) VALUES (?, ?, ?)";
    msg.payload = [newId, msg.payload.porcent, msg.payload.horario];

    return msg;
}

// Se a mensagem não é do tópico esperado, retorna null
return null;

if (Array.isArray(msg.payload) && msg.payload.length > 0) {
    var lastId = msg.payload[0].last_id || 0; // Garantir que last_id é 0 se não existir
    global.set('LastId', lastId);  // Armazena o último ID
    msg.payload = lastId;
} else {
    msg.payload = 0; // Se nenhum ID for encontrado, inicia com 0
}
return msg;

let message = msg.payload;

if (message === "ativar") {
    // Obter a data e hora atual
    var now = new Date();

    // Formatar dia, mês, ano, hora e minuto
    var day = now.getDate();
    var month = now.getMonth() + 1; // getMonth() é zero-based
    var year = now.getFullYear();
    var hours = now.getHours();
    var minutes = now.getMinutes();

    // Adicionar zero à esquerda se o dia, mês, hora ou minuto for menor que 10
    day = day < 10 ? '0' + day : day;
    month = month < 10 ? '0' + month : month;
    hours = hours < 10 ? '0' + hours : hours;
    minutes = minutes < 10 ? '0' + minutes : minutes;

    // Formatar o caminho do arquivo
    var filePath = `C:/MigBiguetiDashboard/backup/${day}-${month}-${year}-${hours}-${minutes}.xlsx`;

    // Definir o caminho do arquivo no msg.filepath
    msg.filepath = filePath;

    // Não definir msg.payload
    delete msg.payload; // Garante que msg.payload não esteja definido

    // Retornar a mensagem
    return msg;
} else {
    // Retornar null se a condição não for atendida
    return null;
}

// Verifica se msg.payload é um array e não está vazio
if (msg.payload && Array.isArray(msg.payload)) {
    // Cria um conjunto para armazenar status já vistos
    const seen = new Set();

    // Filtra os valores para ignorar duplicatas
    const uniqueItems = msg.payload.filter(item => {
        const key = item.status + item.datahora; // Chave para verificar duplicatas
        if (!seen.has(key)) {
            seen.add(key);
            return true; // Mantém o item se não for duplicado
        }
        return false; // Ignora duplicados
    });

    msg.payload = uniqueItems; // Atualiza o payload com itens únicos
    return msg; // Retorna a mensagem com o payload filtrado
} else {
    // Se o payload estiver vazio ou não existir, retorna um payload vazio
    msg.payload = [];
    return msg;
}

// Prepare a query for the SQL database
msg.topic = "SELECT status AS Status, datahora AS Hora, dia AS Dia, mes AS Mês, ano AS Ano FROM historicportao";
return msg;

// Obtém a mensagem do payload
let message = msg.payload;

// Verifica se a mensagem é "Connect"
if (message === "Connect") {
    return null;
} else {
    return msg;
}

// Obtém os dados do contexto de fluxo ou cria novos se não existirem
let estado = context.get('estado') || null;
let horario = context.get('horario') || null;

// Verifica se a mensagem recebida não é uma mensagem inicial
if (msg.topic === "mig/porta/PortaS") {
    // Se a mensagem é "PortaoAberto" ou "PortaoFechado", atualiza o estado
    if (msg.payload === "PortaAberta" || msg.payload === "PortaFechada") {
        estado = msg.payload;
        context.set('estado', estado);
    } 
    // Se a mensagem é um horário, atualiza o horário
    else if (msg.payload.match(/^\d{2}:\d{2}:\d{2}$/)) {
        horario = msg.payload;
        context.set('horario', horario);
    }
}

// Se ambos estado e horário estão disponíveis, cria um objeto com eles
if (estado !== null && horario !== null) {
    msg.payload = {
        estado: estado,
        horario: horario
    };

    // Limpa os valores após criar o objeto para evitar associações erradas
    context.set('estado', null);
    context.set('horario', null);

    return msg;
}

// Se ainda não está pronto para enviar, retorna uma mensagem vazia
return null;

// Verifica se a mensagem recebida é do tópico correto
if (msg.topic === "mig/porta/PortaS") {
    // Configura a mensagem para inserção no MySQL
    msg.topic = "INSERT INTO historicporta (status, datahora, dia, mes, ano) VALUES (?, ?, ?, ?, ?)";

    // Obtém a data atual
    const now = new Date();
    const dia = now.getDate();       // Dia atual
    const mes = now.getMonth() + 1;  // Mês atual (getMonth() retorna 0-11, então adicionamos 1)
    const ano = now.getFullYear();   // Ano atual

    msg.payload = [
        msg.payload.estado,  // Status
        msg.payload.horario, // Horário
        dia,                 // Dia
        mes,                 // Mês
        ano                  // Ano
    ];

    return msg;
}

// Se a mensagem não é do tópico esperado, retorna null
return null;

// Recebe o timestamp do date picker
const timestamp = msg.payload;

// Converte o timestamp para uma data
const date = new Date(timestamp);

// Extrai o dia, mês e ano
const dia = date.getDate();
const mes = date.getMonth() + 1; // getMonth() retorna 0-11, então adicionamos 1
const ano = date.getFullYear();

// Configura o SQL para buscar status e datahora desse dia
msg.topic = "SELECT status, datahora FROM historicportao WHERE dia = ? AND mes = ? AND ano = ?";
msg.payload = [dia, mes, ano];

// Reseta a lista antes da nova consulta
msg.reset = true;

return msg;

// Verifica se o payload retornado do MySQL está vazio
if (msg.payload.length === 0) {
    // Se não houver resultados, configura uma mensagem
    msg.payload = "Nenhum Valor Encontrado";
} else {
    // Se houver resultados, mantém os dados do banco
    msg.reset = true; // Adiciona sinalizador de reset
    msg.payload = msg.payload; 
}

return msg;

// Armazena o estado da garra em contexto
let estadoGarra = context.get("estadoGarra") || "Fechar Garra";

// Verifica a mensagem recebida
if (msg.payload === "AÇÃO") {
    // Alterna o estado
    if (estadoGarra === "Fechar Garra") {
        estadoGarra = "Abrir Garra";
    } else {
        estadoGarra = "Fechar Garra";
    }

    // Armazena o novo estado no contexto
    context.set("estadoGarra", estadoGarra);

    // Atualiza o payload com o novo estado
    msg.payload = estadoGarra; // Muda para o texto que será exibido no botão
    return msg; // Retorna a mensagem para o próximo nó
}

// Se a mensagem não for "AÇÃO", não faz nada
return null;
